mapper.select:
SELECT DISTINCT f.flight_id, f.flight_number, f.departure_airport, f.arrival_airport, f.departure_time, f.arrival_time, f.flight_duration,
f.airplane_id FROM flights f JOIN FlightSeats fs ON f.flight_id = fs.flight_id JOIN SeatTemplate st ON fs.seat_id = st.seat_id
WHERE f.departure_airport = #{departure} AND f.arrival_airport = #{arrival}


	AND DATE(f.departure_time) = #{departureDate}



	AND DATE(f.arrival_time) = #{arrivalDate}



	AND st.seat_class = #{seatClass}



	AND fs.is_available = 1



	GROUP BY f.flight_id HAVING COUNT(fs.seat_id) >= #{passengers}

SELECT DISTINCT f.flight_id, f.departure_airport, f.arrival_airport, f.departure_time, f.arrival_time, f.flight_duration, f.airplane_id,
COUNT(fs.seat_id) AS availableSeats FROM flights f JOIN FlightSeats fs ON f.flight_id = fs.flight_id JOIN SeatTemplate st
ON fs.seat_id = st.seat_id WHERE f.departure_airport = #{departure} AND f.arrival_airport = #{arrival} 


	AND DATE_FORMAT(f.departure_time, '%Y-%m-%d') = #{departureDate}



	AND DATE_FORMAT(f.departure_time, '%Y-%m-%d') = #{arrivalDate}



	AND st.seat_class = #{seatClass}



	AND fs.is_available = 1



	GROUP BY f.flight_id HAVING COUNT(fs.seat_id) >= #{passengers}

SELECT * FROM flights where departure_time

		>= #{selectedDate}


		>=now()

order by departure_time asc LIMIT #{offset}, #{itemsPerPage}

SELECT COUNT(*) FROM flights WHERE departure_time

		>= #{selectedDate}


		>=now()

SELECT * FROM flights WHERE (#{departureAirport} IS NULL OR departure_airport = #{departureAirport}) AND (#{arrivalAirport} IS NULL OR arrival_airport = #{arrivalAirport}) AND departure_time
>= #{selectedDate}
>=now()
AND departure_time < DATE_ADD(#{selectedDate}, INTERVAL 7 DAY) LIMIT #{offset}, #{itemsPerPage}

SELECT COUNT(*) FROM flights WHERE 1=1

	AND departure_airport = #{departureAirport}


	AND arrival_airport = #{arrivalAirport}


	AND DATE(departure_time) >= #{selectedDate}

SELECT COUNT(*) FROM flights WHERE (#{departureAirport} IS NULL OR departure_airport = #{departureAirport}) AND (#{arrivalAirport} IS NULL OR arrival_airport = #{arrivalAirport}) AND departure_time
>= #{selectedDate}
>=now()
AND departure_time < DATE_ADD(#{selectedDate}, INTERVAL 7 DAY) order by departure_time desc LIMIT #{itemsPerPage} OFFSET #{offset}

SELECT * FROM flights WHERE 1=1

		AND departure_airport = #{departureAirport}


		AND arrival_airport = #{arrivalAirport}


		AND DATE(departure_time) >= #{selectedDate}


		and departure_time >= now()

ORDER BY departure_time ASC LIMIT #{itemsPerPage} OFFSET #{offset}
